mapper.update:
update menu
set URL = #{url,jdbcType=VARCHAR},
  NAME = #{name,jdbcType=VARCHAR},
  PARENTID = #{parentid,jdbcType=VARCHAR}
where MENUID = #{menuid,jdbcType=VARCHAR}

mapper.select:
select MENUID, URL, NAME, PARENTID
from menu
where MENUID = #{menuid,jdbcType=VARCHAR}

select MENUID, URL, NAME, PARENTID
from menu where 1=1

  and menuId=#{menuId}


  and name =#{name}
>
