mapper.update:
update user
set name = #{name,jdbcType=VARCHAR},
  password = #{password,jdbcType=VARCHAR},
  level = #{level,jdbcType=VARCHAR},
  roleId = #{roleid,jdbcType=VARCHAR}
where id = #{id,jdbcType=VARCHAR}

mapper.select:
select id, name, password, level, roleId
from user
where id = #{id,jdbcType=VARCHAR}

SELECT t.id as t_id, t. NAME as t_name, t. PASSWORD as t_password, t. LEVEL as t_level, m.menuId as m_menuId, m.url as m_url, m. NAME as m_menuName, m.parentid as m_parentid, r.id as r_id, r.name as r_name FROM USER t LEFT JOIN role_menu k ON t.roleId = k.roleId LEFT JOIN menu m ON m.menuId = k.menuId LEFT JOIN role r on t.roleId=r.id where 1=1
and t.name = #{name,jdbcType=VARCHAR}
and t.id = #{id,jdbcType=VARCHAR}

select id , name, password, level, roleId
from user where 1=1

    and name = #{name,jdbcType=VARCHAR}


    and password = #{password,jdbcType=VARCHAR}
